mapper.select:
SELECT rnum, pno, pid, clarge, cmedium, csmall, pname, bname, pprice
FROM(
	SELECT ROWNUM as rnum, pno, pid, clarge, cmedium, csmall, pname, bname, pprice
	FROM(
	    select p.pno as pno, p.pid as pid, p.clarge as clarge, p.cmedium as cmedium, p.csmall as csmall, p.pname as pname, b.bname as bname, p.pprice as pprice
        from product p join brand b
        on p.bno = b.bno
        order by p.pno
		)
	WHERE ROWNUM <= #{pager.endRowNo}
	)
WHERE rnum >= #{pager.startRowNo}

SELECT
pid, ccolorcode, ssize, samount
FROM stock
WHERE pid =
#{pid}
AND ccolorcode = #{colorcode}

SELECT rnum, bname, pid,pname, ccolorcode, ssize, samount, ptotalamount, stotalamount
FROM(
	SELECT ROWNUM as rnum, bname, pid, pname, ccolorcode, ssize, samount, ptotalamount, stotalamount
	FROM(
	    select b.bname, s.pid,p.pname, s.ccolorcode, s.ssize, s.samount, p.ptotalamount, s.stotalamount
        from brand b, product p, stock s
        where b.bno = p.bno and p.pid = s.pid
        order by pid
		)
	WHERE ROWNUM <= #{pager.endRowNo}
	)
WHERE rnum >= #{pager.startRowNo}

mapper.update:
update stock
set samount=#{samount}
where pid=#{pid} and ccolorcode=#{ccolorcode} and ssize=#{ssize}

update product
set pid=#{product.pid}, bno=#{product.bno}, clarge=#{product.clarge}, cmedium=#{product.cmedium}, csmall=#{product.csmall}, pname=#{product.pname},
pprice=#{product.pprice}, pdetail=#{product.pdetail}, pseason=#{product.pseason}
where pid=#{beforepid}

update color
set ccolorcode=#{color.ccolorcode}, cimage1=#{color.cimage1}, cimage2=#{color.cimage2}, cimage3=#{color.cimage3}, ccolorimage=#{color.ccolorimage}, cmatchpid=#{color.cmatchpid}
where pid=#{beforepid} and ccolorcode=#{beforecolor}

update stock
set ssize=#{stock.ssize}, samount=#{stock.samount}
where pid=#{pid} and ccolorcode=#{color} and ssize=#{size}

update exhibition
set orderno = #{orderno}
where exid = #{exid}

mapper.select:
select distinct cmedium
from category
where clarge=#{clarge}

select csmall
from category
where clarge=#{clarge} and cmedium=#{cmedium}
